import React,{Component} from "react";
import { useState, useEffect } from 'react';
import {BrowserRouter as Router,Route,Switch,NavLink,Link} from "react-router-dom";
import Home from './Home';
import Events from './events.component';
import EventsDetails from './events-detail.component';
import EventsAdd from './events-add.component';
import EventsEdit from './events-edit.component';
import PrivateRoute from './utils/PrivateRoute';
import PublicRoute from './utils/PublicRoute';
import { getToken, removeUserSession, setUserSession } from '../utils/Common';

import axios from 'axios';

import Login from '../Login';
import Dashboard from '../Dashboard';

  export default class Main extends Component {
  render() {
      
    const [authLoading, setAuthLoading] = useState(true);

    useEffect(() => {
      const token = getToken();
      if (!token) {
        return;
      }
  
      axios.get(`http://localhost:4000/verifyToken?token=${token}`).then(response => {
        setUserSession(response.data.token, response.data.user);
        setAuthLoading(false);
      }).catch(error => {
        removeUserSession();
        setAuthLoading(false);
      });
    }, []);
  
    if (authLoading && getToken()) {
      return <div className="content">Checking Authentication...</div>
    }  

      return (
          <div>

              <Router>
                  <div className="container">
                      <nav className="navbar navbar-expand-lg navbar-light bg-light">
                          <Link to={'/'} className="navbar-brand">Admin Portal</Link>
                          <div className="collapse navbar-collapse" id="navbarSupportedContent">
                              <ul className="navbar-nav mr-auto">
                                  <li className="nav-item">
                                      <Link to={'/'} className="nav-link">Home</Link>
                                  </li>
                                  <li className="nav-item">
                                    <Link to={'/login'} className="nav-link">Login</Link>
                                  </li>
                                  <li className="nav-item">
                                    <Link to={'/dashboard'} className="nav-link">Dashboard</Link>
                                  </li>

                                  <li className="nav-item">
                                      <Link to={'/events'} className="nav-link">Events</Link>
                                  </li>
                              </ul>
                          </div>
                      </nav> <br />
                      <Switch>
                          <Route exact path='/' component={Home} />
                          <PublicRoute path="/login" component={Login} />
                          <PrivateRoute path='/events' component={Events} />
                          <PrivateRoute path='/events-add' component={EventsAdd} />
                          <PrivateRoute path='/events-detail/:id' component={EventsDetails} />
                          <PrivateRoute path='/events-edit/:id' component={EventsEdit} />
                      </Switch>
                  </div>
              </Router>
          </div>
      )
  }
}
